document.addEventListener("DOMContentLoaded", function () {
    console.log("Website đã load thành công!");

    // Hiệu ứng hover cho card phim
    const movieCards = document.querySelectorAll(".movie-card");
    movieCards.forEach(card => {
        card.addEventListener("mouseover", () => {
            card.style.transform = "scale(1.08)";
            card.style.boxShadow = "0 4px 15px rgba(255, 204, 0, 0.7)";
        });

        card.addEventListener("mouseout", () => {
            card.style.transform = "scale(1)";
            card.style.boxShadow = "none";
        });
    });

    // Chức năng Dark Mode
    const toggleThemeBtn = document.getElementById("toggle-theme");
    const body = document.body;
    toggleThemeBtn.addEventListener("click", function () {
        body.classList.toggle("light-mode");
        if (body.classList.contains("light-mode")) {
            localStorage.setItem("theme", "light");
            toggleThemeBtn.innerText = "🌙 Dark Mode";
        } else {
            localStorage.setItem("theme", "dark");
            toggleThemeBtn.innerText = "☀️ Light Mode";
        }
    });

    // Kiểm tra trạng thái Dark Mode từ LocalStorage
    if (localStorage.getItem("theme") === "light") {
        body.classList.add("light-mode");
        toggleThemeBtn.innerText = "🌙 Dark Mode";
    }

    // Chức năng tìm kiếm phim theo tên
    const searchInput = document.getElementById("search-movie");
    searchInput.addEventListener("keyup", function () {
        const searchValue = searchInput.value.toLowerCase();
        movieCards.forEach(card => {
            const title = card.querySelector(".movie-title").innerText.toLowerCase();
            if (title.includes(searchValue)) {
                card.style.display = "block";
            } else {
                card.style.display = "none";
            }
        });
    });
});
